var link=document.querySelector(".contacts-button");var popup=document.querySelector(".modal-login");var close=popup.querySelector(".modal-close");var form=popup.querySelector(".form-message");var login=popup.querySelector("[name=user-name]");var password=popup.querySelector("[name=email]");var isStorageSupport=true;var storage="";try{storage=localStorage.getItem("login")}catch(err){isStorageSupport=false}link.addEventListener("click",function(a){a.preventDefault();popup.classList.add("modal-show");if(storage){login.value=storage;password.focus()}else{login.focus()}});close.addEventListener("click",function(a){a.preventDefault();popup.classList.remove("modal-show");popup.classList.remove("modal-error")});form.addEventListener("submit",function(a){if(!login.value||!password.value){a.preventDefault();popup.classList.remove("modal-error");popup.offsetWidth=popup.offsetWidth;popup.classList.add("modal-error");console.log("Нужно ввести имя и имеил")}else{localStorage.setItem("login",login.value)}if(isStorageSupport){localStorage.setItem("login",login.value)}});window.addEventListener("keydown",function(a){if(a.keyCode===27){a.preventDefault();if(popup.classList.contains("modal-show")){popup.classList.remove("modal-show");popup.classList.remove("modal-error")}}});var mapLink=document.querySelector(".contacts-button-map");var mapPopup=document.querySelector(".modal-map");var mapClose=mapPopup.querySelector(".modal-close");mapLink.addEventListener("click",function(a){a.preventDefault();mapPopup.classList.add("modal-show")});mapClose.addEventListener("click",function(a){a.preventDefault();mapPopup.classList.remove("modal-show")});window.addEventListener("keydown",function(a){if(a.keyCode===27){if(mapPopup.classList.contains("modal-show")){mapPopup.classList.remove("modal-show")}}});var link=document.querySelector(".buy");var popup2=document.querySelector(".modal-cart");var close=popup2.querySelector(".modal-close");link.addEventListener("click",function(a){a.preventDefault();popup2.classList.add("modal-show")});close.addEventListener("click",function(a){a.preventDefault();popup2.classList.remove("modal-show")});window.addEventListener("keydown",function(a){if(a.keyCode===27){a.preventDefault();if(popup2.classList.contains("modal-show")){popup2.classList.remove("modal-show")}}});